"""
Sequence alignment algorithm: Needleman-Wunsch
Time complexity: O(len(x)*len(y)) where x and y are the input sequences
"""

class SequenceAlignment():
    def __init__(self, x, y, match_award, mismatch_penalty, gap_penalty):
        self.x = x
        self.y = y
        self.match_award = match_award
        self.mismatch_penalty = mismatch_penalty
        self.gap_penalty = gap_penalty
        self.traceback = []

    def printMatrix(self, mat):
        for i in range(0, len(mat)):
            print('[', end='')
            for j in range(0, len(mat[i])):
                print(mat[i][j], end='')
                if j != len(mat[i]) - 1:
                    print('\t', end='')
            print(']\n')

    def matchScore(self, alpha, beta):
        if alpha == beta:
            return self.match_award
        elif beta == '-' or alpha == '-':
            return self.gap_penalty
        else:
            return self.mismatch_penalty
   
    def tracebackSteps(self, OPT, m, n):                                    # O(n+m)
        align_x = ''
        align_y = ''
        i = m
        j = n

        while i > 0 and j > 0:
            score_cur = OPT[i][j]
            score_diag = OPT[i-1][j-1]
            score_up = OPT[i][j-1]
            score_left = OPT[i-1][j]
       
            if score_cur == score_diag + self.matchScore(self.x[i-1], self.y[j-1]):
                align_x += self.x[i-1]
                align_y += self.y[j-1]
                i -= 1
                j -= 1
            elif score_cur == score_up + gap_penalty:
                align_x += self.x[i-1]
                align_y += '-'
                i -= 1
            elif score_cur == score_left + gap_penalty:
                align_x += '-'
                align_y += self.y[j-1]
                j -= 1

        while i > 0:
            align_x += self.x[i-1]
            align_y += '-'
            i -= 1

        while j > 0:
            align_x += '-'
            align_y += self.y[j-1]
            j -= 1
   
        align_x = align_x[::-1]
        align_y = align_y[::-1]

        return (align_x, align_y)

    def needlemanWunsch(self):                                          # O(n*m)
        m = len(self.x)
        n = len(self.y)
        OPT = [[0 for i in range(n+1)] for j in range(m+1)]             # init zero 2-D array of size (m+1)*(n+1)

        for i in range(1, m+1):                                         # fill penalties in first row/column
            OPT[i][0] = i * -2

        for j in range(1, n+1):
            OPT[0][j] = j * -2

        print(OPT[0])

        for i in range(1, m+1):                                         # O(n*m)
            for j in range(1, n+1):
                match = OPT[i-1][j-1] + self.matchScore(self.x[i-1], self.y[j-1])
                delete = OPT[i-1][j] + gap_penalty
                insert = OPT[i][j-1] + gap_penalty
                # ~
                OPT[i][j] = max(match, delete, insert)

        # self.printMatrix(OPT)

        alignments = self.tracebackSteps(OPT, m, n)

        return (OPT[m][n], alignments)


